function runCommandsThenDo(commands, thenDo) {
    var defaultOptions = ['--no-pager'], self = this,
        cwd = this.get('GitControlTabs').cwd || lively.shell.rootDirectory || lively.shell.getWorkingDirectory();
    commands.forEach(function(ea) {
        if (!ea.gitCommand) return;
        if (Object.isArray(ea.gitCommand)) ea.gitCommand = ea.gitCommand.join(' ');
        ea.command = ['git'].concat(defaultOptions).concat([ea.gitCommand]).join(' ');
        ea.options = ea.options || {};
        ea.options.group = 'GitControl';
        ea.options["cwd"] = cwd;
    });
    return lively.ide.CommandLineInterface.runAll(commands, function(err, result) {
        Object.keys(result).forEach(function(name) {
            var cmd = result[name];
            if (!cmd || !cmd.isShellCommand) { return; } 
            result['cmd' + name.capitalize()] = cmd;
            result[name] = cmd.resultString(true);
        });
        thenDo && thenDo.call(self, result);
    });
}

