function addBuildMorph(m,style) {
    m.index = this.buildIndex++;
    var existing = this.submorphs.detect(function(ea) { return ea.index === m.index; });
    var morphBefore = this.submorphs.detect(function(ea) { return ea.index === m.index - 1; })
                   || this.submorphs.last();
    if (this.buildSettings.style) m.applyStyle(this.buildSettings.style);
    if (style) m.applyStyle(style);
    for (var name in this.buildSettings) {
        if (name === 'style') continue;
        m[name] = this.buildSettings[name];
    }
    if (existing) {
        m.setPosition(existing.getPosition());
        if (this.buildOverwrite) {
            existing.remove();
        } else  {
            this.submorphs.forEach(function(ea) { if (ea.index >= m.index) ea.index++; });
        }
    } else if (morphBefore) {
        m.align(m.bounds().topLeft(), morphBefore.bounds().topLeft().addXY(0,1));
    } else {
        m.setPosition(pt(0,0));
    }
    // m.applyStyle({fill: Color.red.lighter(), resizeWidth: false});
    return this.addMorph(m);
}

