function addTodoItem() {
    var item = this.get('TodoItemProto').copy();
    item.setVisible(true);
    this.addMorph(item).setPosition(this.innerBounds().bottomLeft());
    var self = this;
    // there is a layouting issue when layting manually so trigger layout again...
    (function() { self.applyLayout() }).delay(0);
    this.updateProgress();
    return item;
}

