function todoItemNext(item, before) {
    if (!item) return null;
    var sorted = this.todoItems().sortBy(function(ea) { return ea.getPosition().y }),
        itemIdx = sorted.indexOf(item);
    if (!before && sorted.length-1 === itemIdx) {
        sorted.push(this.addTodoItem());
    }
    var nextIdx = Math.min(sorted.length - 1, Math.max(0, itemIdx + (before ? -1 : 1)));
    return sorted[nextIdx];
}

