function buildLogLines(func) {
    /*
    this.initUpdate()
    */
    var commands = [{
        name: "log",
        options: {ansiAttributeEscape: false},
        gitCommand: "log"
                  + " --graph"
                  + " --pretty=format:"
                  +   "'%Cred%h%Creset "
                  +   "-%C(green)%d%Creset "
                  +   "%C(bold)%s%Creset "
                  +   "%Cblue(%an, %ar)%Creset'"
                  + " --abbrev-commit"
                  + " --date=relative"
                  + " -n 100"
                  + " --all"}];
    this.runCommandsThenDo(commands, function(mapping) {
        var lines = mapping.log.split('\n'), result = {};
        result.logLines = lines;
        func(result);
    });
}

